const clock=()=>{var e=new Date,t=e.getHours(),o=e.getMinutes(),e=e.getSeconds(),t=30*t+.5*o,o=6*o+.1*e,e=6*e,r=document.querySelector("#hour"),c=document.querySelector("#minute"),n=document.querySelector("#second");r.style.transform="rotate("+t+"deg)",c.style.transform="rotate("+o+"deg)",n.style.transform="rotate("+e+"deg)"};setInterval(clock,100);